<?phpfunction proc_meminfo(){	$meminfo = array();	$count = preg_match_all('/(?<=^|[\r\n])([^:]+?)\s*:\s*(.*)(?=$|[\r\n])/', file_get_contents("/proc/meminfo"), $matches);	for ($i=0; $i<$count; $i++) {		$meminfo[$matches[1][$i]] = $matches[2][$i];	}	return $meminfo;}function proc_cpuinfo(){	$cpuinfo = array();	$count = preg_match_all('/(?<=^|[\r\n])([^:]+?)\s*:\s*(.*)(?=$|[\r\n])/', file_get_contents("/proc/cpuinfo"), $matches);	for ($i=0; $i<$count; $i++) {		$cpuinfo[$matches[1][$i]] = $matches[2][$i];	}	return $cpuinfo;}function proc_stat(){	$stat = array();	$count = preg_match_all('/(?<=^|[\r\n])([^\s]+)\s+(.*)(?=$|[\r\n])/', file_get_contents("/proc/stat"), $matches);	for ($i=0; $i<$count; $i++) {		$stat[$matches[1][$i]] = preg_split ('/\s+/', $matches[2][$i], 0, PREG_SPLIT_NO_EMPTY);	}	return $stat;}function proc_diskstats(){	$diskstats = array();	$count = preg_match_all('/(?<=^|[\r\n])\s+([\d]+)\s+([\d]+)\s+([\w\d]+)\s+([\d\s]+)(?=$|[\r\n])/', file_get_contents("/proc/diskstats"), $matches);	for ($i=0; $i<$count; $i++) {		$diskstats[$matches[3][$i]] = preg_split ('/\s+/', $matches[0][$i], 0, PREG_SPLIT_NO_EMPTY);	}	return $diskstats;}function proc_loadavg(){	return preg_split ('/\s+/', file_get_contents("/proc/loadavg"), 0, PREG_SPLIT_NO_EMPTY);}function proc_net_dev(){	$net_dev = array();	$count = preg_match_all('/(?<=^|[\r\n])\s*([\w\d]+):([\d\s]+)(?=$|[\r\n])/', file_get_contents("/proc/net/dev"), $matches);	for ($i=0; $i<$count; $i++) {		$net_dev[$matches[1][$i]] = preg_split ('/\s+/', $matches[2][$i], 0, PREG_SPLIT_NO_EMPTY);	}	return $net_dev;}function proc_mounts(){	$mounts = array();	$count = preg_match_all('/(?<=^|[\r\n])([\S]+)\s+(\S+)\s+(\S+)\s+(\S+)\s+(\d+)\s+(\d+)(?=$|[\r\n])/', file_get_contents("/proc/mounts"), $matches, PREG_SET_ORDER);	for ($i=0; $i<$count; $i++) {		array_push($mounts, array_slice($matches[$i], 1));	}	return $mounts;}function proc_partitions(){	$partitions = array();	$count = preg_match_all('/(?<=^|[\r\n])\s+(\d+)\s+(\d+)\s+(\d+)\s+([\w\d]+)(?=$|[\r\n])/', file_get_contents("/proc/partitions"), $matches, PREG_SET_ORDER);	for ($i=0; $i<$count; $i++) {		array_push($partitions, array_slice($matches[$i], 1));	}	return $partitions;}?>