<?phpfunction system_byteval($content){    $count = preg_match('/(\d+)\s*(\w+)/', $content, $matches);	if ($count == 0) return intval($content);    $unit = strtolower($matches[2]);	$result = intval($matches[1]);    switch ($unit) {    case 'kb':        $result = $result*1024;        break;    case 'mb':        $result = $result*1024*1024;        break;    case 'gb':        $result = $result*1024*1024*1024;        break;    case 'tb':        $result = $result*1024*1024*1024*1024;        break;    default:        break;    }    return $result;}